mapper.update:
UPDATE article
SET like_count = like_count + 1
WHERE articleid = #{articleId};

UPDATE article
SET like_count = like_count - 1
WHERE articleid = #{articleId};

UPDATE article
SET collect_count = collect_count + 1
WHERE articleid = #{articleId};

UPDATE article
SET collect_count = collect_count - 1
WHERE articleid = #{articleId};

UPDATE article
SET comment_count = comment_count + 1
WHERE articleid = #{articleId};

UPDATE article
SET comment_count = comment_count - 1
WHERE articleid = #{articleId};
